import React from 'react'
import './page-icon.css'

const PageIcon = ({imageURL, pageName, currentPage, switchPage, device}) => {
     const desktop = [
          <>
               <div className='page-name-box'>{pageName}</div>
               <img alt = {pageName} src={imageURL} width='40px' height='auto'/>
          </>
     ]
     const mobile = [
          <div className='page-name-text'>{pageName}</div>
          
     ]
     if (device == 'desktop') {
          return (pageName === currentPage) 
          ? <div className='icon-container' data-page-name = {pageName} style={{border: "5px solid black"}} onClick={switchPage}> {desktop} </div>
          : <div className='icon-container' data-page-name = {pageName} onClick={switchPage}> {desktop} </div>
     }
     else if (device == 'mobile') {
          return (pageName === currentPage) 
          ? <div data-page-name = {pageName} style={{fontWeight: 'bold', borderBottom: '3px solid green'}} onClick={switchPage}> {mobile} </div>
          : <div data-page-name = {pageName} onClick={switchPage}> {mobile} </div>
     }
     
}

export default PageIcon
