import React, { Component } from 'react';

import '../index.css';
import './App.css';

import PageIcon from '../components/page-icon';
import CurrentPage from './current-page';

class App extends Component {
  constructor() {
    super()
    this.state = {
      page: 'Home',
      isWaiting: true,
      device: undefined,
      factor: 0
    }
  }

  handleResize = () => (window.innerWidth >= 1190) ? this.setState({device: 'desktop'}) : this.setState({device: 'mobile'})
  
  adjustFactor = () => {
    a = document.getElementsByClassName('page-desktop')
    a.onscroll = (e) => {
      console.log(a.scrollTop)
    }
  }

  componentDidMount() {
    this.handleResize()
    this.adjustFactor()
    window.addEventListener('resize', this.handleResize)
  }

  switchPage = (event) => {
    const pageName = event.target.alt || event.target.getAttribute('data-page-name') || event.target.textContent
    console.log(pageName)
    switch (pageName) {
      case 'Home':
        this.setState({ page: 'Home' })
        break
      case 'Expertise':
        this.setState({ page: 'Expertise' })
        break
      case 'Project':
        this.setState({ page: 'Project' })
        break
      case 'Contact':
        this.setState({ page: 'Contact' })
        break
      default:
        console.log('Page switching failed to proceed')
        break
    }
  }

  render() {
    
    if (this.state.isWaiting === true) {
      setTimeout(() => this.setState({ isWaiting: false }), 1000)
      return <button> </button>
    }

    let { page, device } = this.state

    if (device === 'desktop') {
      return <div>

        <div className="svg-container">
          <svg viewBox="0 0 500 2000">
            <path d="m0 0 l450 0 c-200 300 500 600 -800 1500 l-1000 1500 z" fill=''> </path>
            <defs>
              <linearGradient id="gradient-path">
                <stop offset="20%" stop-color="#91e699" />
                <stop offset="60%" stop-color="#8ae38a" />
                <stop offset="120%" stop-color="#094709" />
              </linearGradient>
            </defs>
          </svg>
        </div>

        <div className='avatar'> </div>

        <div className='navigation-desktop'>
          <PageIcon imageURL='./home-icon.png' pageName='Home' currentPage={this.state.page} switchPage={this.switchPage} device={device} />
          <PageIcon imageURL='./expertise-icon.png' pageName='Expertise' currentPage={this.state.page} switchPage={this.switchPage} device={device} />
          <PageIcon imageURL='./project-icon.png' pageName='Project' currentPage={this.state.page} switchPage={this.switchPage} device={device} />
          <PageIcon imageURL='./contact-icon.png' pageName='Contact' currentPage={this.state.page} switchPage={this.switchPage} device={device} />
        </div>

        <div className='page-desktop'>
          <CurrentPage pageName={this.state.page} />
        </div>

      </div>
    }

    else if (device === 'mobile') {
      return <div>

        <div style={{ position: 'relative', width: '100vw', height: '200px', background: 'linear-gradient(to top, #8ae38a 0%, #094709 100%)' }}>
          <div style={{ width: '200px', height: '200px', position: 'absolute', inset: '50px 50% 0 50%', transform: 'translateX(-50%)', background: 'url(avatar-square.png)', backgroundRepeat: 'none', backgroundSize: 'cover', backgroundPosition: 'center', borderRadius: '50%' }}> </div>
        </div>

        <div className='navigation-mobile'>
          <PageIcon pageName='Home' currentPage={this.state.page} switchPage={this.switchPage} device={device} />
          <PageIcon pageName='Expertise' currentPage={this.state.page} switchPage={this.switchPage} device={device} />
          <PageIcon pageName='Project' currentPage={this.state.page} switchPage={this.switchPage} device={device} />
          <PageIcon pageName='Contact' currentPage={this.state.page} switchPage={this.switchPage} device={device} />
        </div>

        <div className='page-mobile'>
          <CurrentPage pageName={page} device={device} />
        </div>

      </div>
    }
  }
}

export default App;
