import React, { Component } from 'react';

import '../index.css';
import './App.css';

import PageIcon from '../components/page-icon';
import CurrentPage from './current-page';

class App extends Component {
  constructor() {
    super()
    this.state = {
      page: 'Home',
      isWaiting: true
    }
  }

  // componentDidMount() {
    
  // }

  switchPage = (event) => {
    const pageName = event.target.alt || event.target.getAttribute('data-page-name')
    console.log(pageName)
    switch (pageName) {
      case 'Home':
        this.setState({page:'Home'})
        break
      case 'Expertise':
        this.setState({page:'Expertise'})
        break
      case 'Project':
        this.setState({page:'Project'})
        break
      case 'Contact':
        this.setState({page:'Contact'})
        break
      default:
        console.log('Page switching failed to proceed')
        break
    }
  }

  render() {

    if (this.state.isWaiting === true) {
      setTimeout(() => this.setState({ isWaiting: false }), 1000)
      return <button> </button>
    }

    else return <div>

      <div className = "svg-container"> 
        <svg id="wave" viewBox="0 0 1440 450" version="1.1" xmlns="http://www.w3.org/2000/svg"><defs><linearGradient id="sw-gradient-0" x1="0" x2="0" y1="1" y2="0"><stop stop-color="rgba(83.004, 187.033, 98.769, 1)" offset="0%"></stop><stop stop-color="rgba(11.886, 98.238, 10.318, 1)" offset="100%"></stop></linearGradient></defs><path d="M0,90L40,112.5C80,135,160,180,240,210C320,240,400,255,480,225C560,195,640,120,720,75C800,30,880,15,960,15C1040,15,1120,30,1200,82.5C1280,135,1360,225,1440,217.5C1520,210,1600,105,1680,52.5C1760,0,1840,0,1920,60C2000,120,2080,240,2160,300C2240,360,2320,360,2400,345C2480,330,2560,300,2640,292.5C2720,285,2800,300,2880,255C2960,210,3040,105,3120,105C3200,105,3280,210,3360,247.5C3440,285,3520,255,3600,262.5C3680,270,3760,315,3840,322.5C3920,330,4000,300,4080,240C4160,180,4240,90,4320,97.5C4400,105,4480,210,4560,277.5C4640,345,4720,375,4800,337.5C4880,300,4960,195,5040,142.5C5120,90,5200,90,5280,112.5C5360,135,5440,180,5520,195C5600,210,5680,195,5720,187.5L5760,180L5760,450L5720,450C5680,450,5600,450,5520,450C5440,450,5360,450,5280,450C5200,450,5120,450,5040,450C4960,450,4880,450,4800,450C4720,450,4640,450,4560,450C4480,450,4400,450,4320,450C4240,450,4160,450,4080,450C4000,450,3920,450,3840,450C3760,450,3680,450,3600,450C3520,450,3440,450,3360,450C3280,450,3200,450,3120,450C3040,450,2960,450,2880,450C2800,450,2720,450,2640,450C2560,450,2480,450,2400,450C2320,450,2240,450,2160,450C2080,450,2000,450,1920,450C1840,450,1760,450,1680,450C1600,450,1520,450,1440,450C1360,450,1280,450,1200,450C1120,450,1040,450,960,450C880,450,800,450,720,450C640,450,560,450,480,450C400,450,320,450,240,450C160,450,80,450,40,450L0,450Z"></path></svg>
      </div>

      <div className = 'avatar'> </div>

      <div className = 'navigation'> 
        <PageIcon imageURL='./home-icon.png' pageName = 'Home' currentPage={this.state.page} switchPage = {this.switchPage} />
        <PageIcon imageURL='./expertise-icon.png' pageName = 'Expertise' currentPage={this.state.page} switchPage = {this.switchPage} />
        <PageIcon imageURL='./project-icon.png' pageName = 'Project' currentPage={this.state.page} switchPage = {this.switchPage} />
        <PageIcon imageURL='./contact-icon.png' pageName = 'Contact' currentPage={this.state.page} switchPage = {this.switchPage} />
      </div>

      <div className = 'page'>
        <CurrentPage pageName = {this.state.page}/>
      </div>
      

    </div>
  }
}

export default App;
