import React, { Component } from 'react';

import '../index.css';
import './App.css';

import PageIcon from '../components/page-icon';
import CurrentPage from './current-page';

class App extends Component {
  constructor() {
    super()
    this.state = {
      page: 'Home',
      isWaiting: true
    }
  }

  // componentDidMount() {
    
  // }

  switchPage = (event) => {
    const pageName = event.target.alt || event.target.getAttribute('data-page-name')
    console.log(pageName)
    switch (pageName) {
      case 'Home':
        this.setState({page:'Home'})
        break
      case 'Expertise':
        this.setState({page:'Expertise'})
        break
      case 'Project':
        this.setState({page:'Project'})
        break
      case 'Contact':
        this.setState({page:'Contact'})
        break
      default:
        console.log('Page switching failed to proceed')
        break
    }
  }

  render() {

    if (this.state.isWaiting === true) {
      setTimeout(() => this.setState({ isWaiting: false }), 1000)
      return <button> </button>
    }

    else return <div>

      <div className = "svg-container"> 
        <svg id="wave" viewBox="0 0 1440 310" version="1.1" xmlns="http://www.w3.org/2000/svg"><defs><linearGradient id="sw-gradient-0" x1="0" x2="0" y1="1" y2="0"><stop stop-color="rgba(83.004, 187.033, 98.769, 1)" offset="0%"></stop><stop stop-color="rgba(11.886, 98.238, 10.318, 1)" offset="100%"></stop></linearGradient></defs><path d="M0,62L40,77.5C80,93,160,124,240,144.7C320,165,400,176,480,155C560,134,640,83,720,51.7C800,21,880,10,960,10.3C1040,10,1120,21,1200,56.8C1280,93,1360,155,1440,149.8C1520,145,1600,72,1680,36.2C1760,0,1840,0,1920,41.3C2000,83,2080,165,2160,206.7C2240,248,2320,248,2400,237.7C2480,227,2560,207,2640,201.5C2720,196,2800,207,2880,175.7C2960,145,3040,72,3120,72.3C3200,72,3280,145,3360,170.5C3440,196,3520,176,3600,180.8C3680,186,3760,217,3840,222.2C3920,227,4000,207,4080,165.3C4160,124,4240,62,4320,67.2C4400,72,4480,145,4560,191.2C4640,238,4720,258,4800,232.5C4880,207,4960,134,5040,98.2C5120,62,5200,62,5280,77.5C5360,93,5440,124,5520,134.3C5600,145,5680,134,5720,129.2L5760,124L5760,310L5720,310C5680,310,5600,310,5520,310C5440,310,5360,310,5280,310C5200,310,5120,310,5040,310C4960,310,4880,310,4800,310C4720,310,4640,310,4560,310C4480,310,4400,310,4320,310C4240,310,4160,310,4080,310C4000,310,3920,310,3840,310C3760,310,3680,310,3600,310C3520,310,3440,310,3360,310C3280,310,3200,310,3120,310C3040,310,2960,310,2880,310C2800,310,2720,310,2640,310C2560,310,2480,310,2400,310C2320,310,2240,310,2160,310C2080,310,2000,310,1920,310C1840,310,1760,310,1680,310C1600,310,1520,310,1440,310C1360,310,1280,310,1200,310C1120,310,1040,310,960,310C880,310,800,310,720,310C640,310,560,310,480,310C400,310,320,310,240,310C160,310,80,310,40,310L0,310Z"></path></svg>
      </div>

      <div className = 'avatar'> </div>

      <div className = 'navigation'> 
        <PageIcon imageURL='./home-icon.png' pageName = 'Home' currentPage={this.state.page} switchPage = {this.switchPage} />
        <PageIcon imageURL='./expertise-icon.png' pageName = 'Expertise' currentPage={this.state.page} switchPage = {this.switchPage} />
        <PageIcon imageURL='./project-icon.png' pageName = 'Project' currentPage={this.state.page} switchPage = {this.switchPage} />
        <PageIcon imageURL='./contact-icon.png' pageName = 'Contact' currentPage={this.state.page} switchPage = {this.switchPage} />
      </div>

      <div className = 'page'>
        <CurrentPage pageName = {this.state.page}/>
      </div>
      

    </div>
  }
}

export default App;
