import React, { Component } from 'react';

import '../index.css';
import './App.css';

import PageIcon from '../components/page-icon';
import CurrentPage from './current-page';

class App extends Component {
  constructor() {
    super()
    this.state = {
      page: 'Home',
      isWaiting: true
    }
  }

  // componentDidMount() {
    
  // }

  switchPage = (event) => {
    const pageName = event.target.alt || event.target.getAttribute('data-page-name')
    console.log(pageName)
    switch (pageName) {
      case 'Home':
        this.setState({page:'Home'})
        break
      case 'Expertise':
        this.setState({page:'Expertise'})
        break
      case 'Project':
        this.setState({page:'Project'})
        break
      case 'Contact':
        this.setState({page:'Contact'})
        break
      default:
        console.log('Page switching failed to proceed')
        break
    }
  }

  render() {

    if (this.state.isWaiting === true) {
      setTimeout(() => this.setState({ isWaiting: false }), 1000)
      return <button> </button>
    }

    else return <div>

      <div className = "svg-container"> 
        <svg width="100%" height="100%" id="svg" viewBox="0 0 1440 700" xmlns="http://www.w3.org/2000/svg" class="transition duration-300 ease-in-out delay-150"><defs><linearGradient id="gradient" x1="0%" y1="50%" x2="100%" y2="50%"><stop offset="5%" stop-color="#00d08488"></stop><stop offset="95%" stop-color="#00d08488"></stop></linearGradient></defs><path d="M 0,700 C 0,700 0,233 0,233 C 117.75,202.60714285714286 235.5,172.21428571428572 337,193 C 438.5,213.78571428571428 523.7499999999999,285.75000000000006 663,316 C 802.2500000000001,346.24999999999994 995.5000000000002,334.7857142857143 1134,314 C 1272.4999999999998,293.2142857142857 1356.25,263.1071428571429 1440,233 C 1440,233 1440,700 1440,700 Z" stroke="none" stroke-width="0" fill="url(#gradient)" class="transition-all duration-300 ease-in-out delay-150 path-0"></path><defs><linearGradient id="gradient" x1="0%" y1="50%" x2="100%" y2="50%"><stop offset="5%" stop-color="#00d084ff"></stop><stop offset="95%" stop-color="#00d084ff"></stop></linearGradient></defs><path d="M 0,700 C 0,700 0,466 0,466 C 121.64285714285714,504.0357142857143 243.28571428571428,542.0714285714286 375,545 C 506.7142857142857,547.9285714285714 648.5,515.75 778,514 C 907.5,512.25 1024.7142857142858,540.9285714285714 1133,538 C 1241.2857142857142,535.0714285714286 1340.642857142857,500.5357142857143 1440,466 C 1440,466 1440,700 1440,700 Z" stroke="none" stroke-width="0" fill="url(#gradient)" class="transition-all duration-300 ease-in-out delay-150 path-1"></path></svg>
      </div>

      <div className = 'avatar'> </div>

      <div className = 'navigation'> 
        <PageIcon imageURL='./home-icon.png' pageName = 'Home' currentPage={this.state.page} switchPage = {this.switchPage} />
        <PageIcon imageURL='./expertise-icon.png' pageName = 'Expertise' currentPage={this.state.page} switchPage = {this.switchPage} />
        <PageIcon imageURL='./project-icon.png' pageName = 'Project' currentPage={this.state.page} switchPage = {this.switchPage} />
        <PageIcon imageURL='./contact-icon.png' pageName = 'Contact' currentPage={this.state.page} switchPage = {this.switchPage} />
      </div>

      <div className = 'page'>
        <CurrentPage pageName = {this.state.page}/>
      </div>
      

    </div>
  }
}

export default App;
