import React, { Component } from 'react';

import './App.css';
import '../index.css';

class App extends Component {
  constructor() {
    super()
    this.state = {
      page: 'Introduction',
      isWaiting: true
    }
  }

  componentDidMount() {
    setTimeout(() => {
      this.setState({ isWaiting: false })
    }, 3000)
  }

  render() {
    // if (this.state.isWaiting === true) {
    //   setTimeout(() => this.setState({ isWaiting: false }), 10000)
    //   return <button> </button>
    // }
    if(window.innerWidth < 768) return <h1> Hello </h1>
    else return <div>

      <div className = "container"> 
        <svg viewBox="0 0 500 500" preserveAspectRatio="xMinYMin meet">
          <path d="M0,100 C150,200 350,0 500,100 L500,00 L0,0 Z" fill = 'rgb(50, 153, 71)'> </path>
        </svg>
      </div>

      <div className = 'avatar'>

      </div>

    </div>
  }
}

export default App;
