import React, { Component } from 'react';

import '../index.css';
import './App.css';

import PageIcon from '../components/page-icon';
import CurrentPage from './current-page';

class App extends Component {
  constructor() {
    super()
    this.state = {
      page: 'Home',
      isWaiting: true
    }
  }

  // componentDidMount() {
    
  // }

  switchPage = (event) => {
    const pageName = event.target.alt || event.target.getAttribute('data-page-name')
    console.log(pageName)
    switch (pageName) {
      case 'Home':
        this.setState({page:'Home'})
        break
      case 'Expertise':
        this.setState({page:'Expertise'})
        break
      case 'Project':
        this.setState({page:'Project'})
        break
      case 'Contact':
        this.setState({page:'Contact'})
        break
      default:
        console.log('Page switching failed to proceed')
        break
    }
  }

  render() {

    if (this.state.isWaiting === true) {
      setTimeout(() => this.setState({ isWaiting: false }), 1000)
      return <button> </button>
    }

    else return <div>

      <div className = "svg-container"> 
        <svg id="wave" viewBox="0 0 1440 270" version="1.1" xmlns="http://www.w3.org/2000/svg"><defs><linearGradient id="sw-gradient-0" x1="0" x2="0" y1="1" y2="0"><stop stop-color="rgba(83.004, 187.033, 98.769, 1)" offset="0%"></stop><stop stop-color="rgba(11.886, 98.238, 10.318, 1)" offset="100%"></stop></linearGradient></defs><path d="M0,54L40,67.5C80,81,160,108,240,126C320,144,400,153,480,135C560,117,640,72,720,45C800,18,880,9,960,9C1040,9,1120,18,1200,49.5C1280,81,1360,135,1440,130.5C1520,126,1600,63,1680,31.5C1760,0,1840,0,1920,36C2000,72,2080,144,2160,180C2240,216,2320,216,2400,207C2480,198,2560,180,2640,175.5C2720,171,2800,180,2880,153C2960,126,3040,63,3120,63C3200,63,3280,126,3360,148.5C3440,171,3520,153,3600,157.5C3680,162,3760,189,3840,193.5C3920,198,4000,180,4080,144C4160,108,4240,54,4320,58.5C4400,63,4480,126,4560,166.5C4640,207,4720,225,4800,202.5C4880,180,4960,117,5040,85.5C5120,54,5200,54,5280,67.5C5360,81,5440,108,5520,117C5600,126,5680,117,5720,112.5L5760,108L5760,270L5720,270C5680,270,5600,270,5520,270C5440,270,5360,270,5280,270C5200,270,5120,270,5040,270C4960,270,4880,270,4800,270C4720,270,4640,270,4560,270C4480,270,4400,270,4320,270C4240,270,4160,270,4080,270C4000,270,3920,270,3840,270C3760,270,3680,270,3600,270C3520,270,3440,270,3360,270C3280,270,3200,270,3120,270C3040,270,2960,270,2880,270C2800,270,2720,270,2640,270C2560,270,2480,270,2400,270C2320,270,2240,270,2160,270C2080,270,2000,270,1920,270C1840,270,1760,270,1680,270C1600,270,1520,270,1440,270C1360,270,1280,270,1200,270C1120,270,1040,270,960,270C880,270,800,270,720,270C640,270,560,270,480,270C400,270,320,270,240,270C160,270,80,270,40,270L0,270Z"></path></svg>
      </div>

      <div className = 'avatar'> </div>

      <div className = 'navigation'> 
        <PageIcon imageURL='./home-icon.png' pageName = 'Home' currentPage={this.state.page} switchPage = {this.switchPage} />
        <PageIcon imageURL='./expertise-icon.png' pageName = 'Expertise' currentPage={this.state.page} switchPage = {this.switchPage} />
        <PageIcon imageURL='./project-icon.png' pageName = 'Project' currentPage={this.state.page} switchPage = {this.switchPage} />
        <PageIcon imageURL='./contact-icon.png' pageName = 'Contact' currentPage={this.state.page} switchPage = {this.switchPage} />
      </div>

      <div className = 'page'>
        <CurrentPage pageName = {this.state.page}/>
      </div>
      

    </div>
  }
}

export default App;
